import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import Registration from './Registration';
import Dashboard from './Dashboard';


function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      setIsAuthenticated(true);
      console.log(setIsAuthenticated);
    }
  }, []);


  return (
    <Router>
      <Routes>
        {/* Redirect base route depending on authentication status */}
        <Route path="/" element={<Navigate replace to={isAuthenticated ? "/dashboard" : "/login"} />} />

        {/* Login route */}
        <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />

        {/* Registration route */}
        <Route path="/register" element={<Registration />} />

        {/* Dashboard route */}
        <Route path="/dashboard" element={isAuthenticated ? <Dashboard onLogout={() => setIsAuthenticated(false)} /> : <Navigate replace to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
