import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './Login';
import Registration from './Registration';
import Dashboard from './Dashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();  // This should be inside the component or a hook.

  // Example of handling login:
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    navigate('/dashboard', { replace: true });  // Correct use of navigate.
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLoginSuccess} />} />
        <Route path="/register" element={<Registration />} />
        <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <Navigate replace to="/login" />} />
        <Route path="/" element={<Login />} />
      </Routes>
    </Router>
  );
}

export default App;
