import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './Login';
import Registration from './Registration';
import Dashboard from './Dashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

   useEffect(() => {
    if (isAuthenticated) {
      // Navigate to dashboard only if authenticated
      navigate('/dashboard', { replace: true });
    }
  }, [isAuthenticated]);

  return (
    <Router>
      <Routes>
        {/* Redirect base route depending on authentication status */}
        <Route path="/" element={<Navigate replace to={isAuthenticated ? "/dashboard" : "/login"} />} />

        {/* Login route */}
        <Route path="/login" element={<Login onLogin={handleLoginSuccess} />} />

        {/* Registration route */}
        <Route path="/register" element={<Registration />} />

        {/* Dashboard route */}
        <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <Navigate replace to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
