import React, { useState } from 'react';
import './Forms.css';
import axios from 'axios';
import { useHistory } from 'react-router-dom';


function Login({ onLogin, onNavigateToRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:6969/login', {
        username,
        password
      });
      localStorage.setItem('jwt', response.data.token);  // Save the token
      onLogin(username, password);
      history.push('/dashboard');  // Redirect to dashboard
    } catch (error) {
      console.error('Login failed:', error.response);
    }
};


  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        <div className="form-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="button">Login</button>
        <button type="button" className="button" onClick={onNavigateToRegister} style={{ marginTop: '10px' }}>
          Register
        </button>
      </form>
    </div>
  );
}

export default Login;
