import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Ensure the path is correct

function Dashboard({ onLogout }) {
    const [books, setBooks] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchBooks();
    }, []);

    const fetchBooks = () => {
        setIsLoading(true);
        setError('');
        const token = localStorage.getItem('jwt');
        axios.get('/books', {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(response => {
            setBooks(response.data);
            setIsLoading(false);
        })
        .catch(error => {
            console.error('Error fetching books:', error);
            setError('Failed to fetch books');
            setIsLoading(false);
            if (error.response && error.response.status === 401) {
                navigate('/login');
            }
        });
    };

    const borrowBook = (id) => {
        const token = localStorage.getItem('jwt');
        axios.get(`/books/${id}`, {}, {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(() => {
            console.log("Book borrowed!");
            fetchBooks(); // Refresh the book list
        })
        .catch(error => {
            console.error('Error borrowing book:', error);
        });
    };

    const returnBook = (id) => {
        const token = localStorage.getItem('jwt');
        axios.get(`/books/${id}`, {}, {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(() => {
            console.log("Book returned!");
            fetchBooks(); // Refresh the book list
        })
        .catch(error => {
            console.error('Error returning book:', error);
        });
    };

    return (
        <div className="container">
            <div className="logout-button">
                <button onClick={onLogout} className="button">Logout</button>
            </div>
            <h1 className="header">Dashboard</h1>
            {isLoading ? (
                <p>Loading books...</p>
            ) : error ? (
                <p>{error}</p>
            ) : books.length > 0 ? (
                <div>
                    <h2>Available Books</h2>
                    <div className="books-list">
                        {books.map(book => (
                            <div key={book.id} className="book-item">
                                <p><strong>Title:</strong> {book.name}</p>
                                <p><strong>Author:</strong> {book.author}</p>
                                <p><strong>Publisher:</strong> {book.publisher}</p>
                                {book.isAvailable ? (
                                    <button onClick={() => borrowBook(book.id)} className="button">Borrow</button>) : (
                                    <button onClick={() => returnBook(book.id)} className="button">Return</button>
                                )}
                            </div>
                        ))}
                    </div>
                </div>
            ) : (
                <p>No books available.</p>
            )}
        </div>
    );
}

export default Dashboard;
